import React from 'react'
import Dice from '../Dice/Dice';
import Player from '../Player/Player';

class GameBoard extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            playerScore1: 15,
            playerScore1: 7,
            diceValue1: null,
            diceValue2: null,
            currentRollValue: 0,
            diceRolled: false,            
            activePlayer: true,
        }
    }
        rollHandler = () => {
            const diceRoll1 = Math.floor((Math.random() *6) + 1)
            const diceRoll2 = Math.floor((Math.random() *6) + 1)
            this.setState({
                diceValue1: diceRoll1,
                diceValue2: diceRoll2,
                currentRollValue: diceRoll1 + diceRoll2,
            })
        }


    render() {
        return (
            <div className='GameBoard'>
                <Player active={this.state.activePlayer} className="playerLeft" playerName="Player 1" totalScore={this.state.playerScore1} currentRoll={this.state.currentRollValue} />
                <Dice rollHandler={this.rollHandler} roll1={this.state.diceValue1} roll2={this.state.diceValue2}/>
                <Player active={!this.state.activePlayer}className="playerRight" playerName="Player 2" totalScore={this.state.playerScore2} currentRoll="0" />
            </div>
        )
    }
}

export default GameBoard;
