import React from 'react'
import Dice from '../Dice/Dice';
import Player from '../Player/Player';

class GameBoard extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            playerScore: 15,
            diceValue1: null,
            diceValue2: null,
            currentRollValue: 8,
            diceRolled: false,

        }
        function rollHandler() {
            this.setState({
                diceValue1: this.state.diceValue1 = Math.floor((Math.random() *6) + 1),
                diceValue2: this.state.diceValue2 = Math.floor((Math.random() *6) + 1)
            })
        }
    }

    render() {
        return (
            <div className='GameBoard'>
                <Player className="playerLeft" playerName="Player 1" totalScore={this.state.playerScore} currentRoll={this.state.currentRollValue} />
                <Dice rollHandler={this.rollHandler} roll1={this.state.diceValue1} roll2={this.state.diceValue2}/>
                <Player className="playerRight" playerName="Player 2" totalScore="0" currentRoll="0" />
            </div>
        )
    }
}

export default GameBoard;
