import Button from './Buttons';

class Controls extends React.Controls {
    constructor(props) {
        super(props);

        this.state = {Value: null}
    }
    clicker = () => {
        this.setState({})
    }


    render() {
        return (
            <div className="Controls">
                <Button onClick function={<button>Roll</button>} />
                <Button function={<button>Hold</button>} />
                <Button function={<button>New Game</button>} />
            </div>
        )
    }
}

export default Controls;
