    constructor(props){
        super(props);

        this.state = {
            Click: null,
        }
    }



    render() {
        return (
            <div>
                <Player playerName="Player 1" totalScore="0" currentRoll="0" />
                <Dice />
                <Player playerName="Player 2" totalScore="0" currentRoll="0" />
                <Controls />
            </div>
        )
    }
}

export default GameBoard;
